    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        float valor1 = Float.parseFloat(a.getValue().toString());
        float valor2 = Float.parseFloat(b.getValue().toString());
        float valor3 = Float.parseFloat(c.getValue().toString());
        float result;
        result = ((float) Math.pow(valor2, 2))-4*(valor1*valor3);
        resultDelta.setText(String.format("%.2f",result));
        float x1,x2;
        x1 = ((-1 * valor2) + (float)Math.sqrt(result)) / (2 * valor1);
        x2 = ((-1 * valor2) - (float)Math.sqrt(result)) / (2 * valor1);
        xLinha.setText(String.format("%.2f",x1));
        xDuasLinhas.setText(String.format("%.2f",x2));
        
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

